#include <stdio.h>
#include <stdlib.h>

void Q8()
{
    int RunLength = 13;
    long long Product = 1;

    char* Str = "";
    char* Head = Str+RunLength;
    char* Base = Str;

    for(int i = 0; i < RunLength; i++)
    {                                           //Calculate a base product to work from
        Product *= *(Base + i) - '0';
    }

    long long Max = Product;

    while(*Head != '\0')
    {
        if(*Head == '0')
        {                                       //If we see zero shift head and base along to avoid the zero...
            int Counter = 0;
            Product = 1;

            while(Counter <= RunLength)
            {
                Head++;
                Base++;
                if(*Head == '0')
                {
                    Counter = 0;
                }
                else
                {
                Counter++;
                }
            }
            for(int i = 0; i < RunLength; i++)
            {                                   //And calculate a new base product
                Product *= *(Base + i) - '0';
            }
        }
        else
        {
            Product = (Product/(*Base - '0'))*(*Head - '0');  //The next product will be the previous product times the digit gained divded by the digit lost
            Head++;
            Base++;
        }

        if(Product > Max)
        {
            Max = Product;
        }
    }
    printf("%lld\n", Max);
}

